IGNORE_CASE = True
MAX_WORKERS = 8        # 并行线程数
FILE_EXT = ".txt"      # 扫描文件类型
CHUNK_SIZE = 1024*1024*10  # 10MB 一次读入
OUTPUT_CSV = "keyword_results.csv"  # 输出 CSV
# -------------------------------------
def process_file(file_path: Path, matcher: ACMatcher):
    """
    分块读取大文件，返回文件的关键词计数 Counter，并统计文件大小和行数
    """
    keyword_counter = Counter()
    total_lines = 0
    total_bytes = 0

    try:
        with file_path.open("r", encoding="utf-8") as f:
            while True:
                chunk = f.read(CHUNK_SIZE)
                if not chunk:
                    break
                total_bytes += len(chunk.encode("utf-8"))
                lines = chunk.splitlines()
                total_lines += len(lines)
                matches = matcher.search(chunk)
                keyword_counter.update(matches)
    except Exception as e:
        print(f"Error processing {file_path}: {e}")
    return file_path, keyword_counter, total_bytes, total_lines

def get_all_text_files(root_dir: Path, ext=FILE_EXT):
    """递归获取所有文本文件"""
    return list(root_dir.rglob(f"*{ext}"))

def batch_search(root_dir: Path, matcher: ACMatcher, max_workers=8):
    """批量搜索文件并统计关键词、文件大小和行数"""
    start_time = time.time()
    files = get_all_text_files(root_dir)
    results = {}
    total_counter = Counter()
    total_bytes_all = 0
    total_lines_all = 0

    with ThreadPoolExecutor(max_workers=max_workers) as executor:
        futures = {executor.submit(process_file, f, matcher): f for f in files}
        for future in tqdm(as_completed(futures), total=len(files), desc="Processing files"):
            file_path, counter, file_bytes, file_lines = future.result()
            results[file_path] = counter
            total_counter.update(counter)
            total_bytes_all += file_bytes
            total_lines_all += file_lines

    end_time = time.time()
    elapsed = end_time - start_time
    print(f"\n=== Total elapsed time: {elapsed:.2f} seconds ===")
    print(f"=== Total files processed: {len(files)} ===")
    print(f"=== Total size: {total_bytes_all / (1024*1024):.2f} MB ===")
    print(f"=== Total lines: {total_lines_all} ===")
    return results, total_counter, total_bytes_all, total_lines_all, elapsed

def save_results_to_csv(results: dict, total_counter: Counter, csv_file: str):
    """保存每个文件的匹配和总计数到 CSV"""
    with open(csv_file, "w", newline="", encoding="utf-8") as f:
        writer = csv.writer(f)
        # 写表头
        writer.writerow(["File"] + list(KEYWORDS))
        for file_path, counter in results.items():
            row = [file_path] + [counter.get(kw, 0) for kw in KEYWORDS]
            writer.writerow(row)
        # 写总计行
        writer.writerow(["TOTAL"] + [total_counter.get(kw, 0) for kw in KEYWORDS])

if __name__ == "__main__":
    print("Building ACMatcher engine...")
    matcher = ACMatcher(KEYWORDS, IGNORE_CASE)

    print("Starting batch search...")
    results, total_counter, total_bytes_all, total_lines_all, elapsed = batch_search(ROOT_DIR, matcher, MAX_WORKERS)

    # 输出每个文件的匹配结果
    for file_path, counter in results.items():
        print(f"{file_path}: {dict(counter)}")

    # 输出总计
    print("\n=== Total Keyword Counts ===")
    for kw, count in total_counter.most_common():
        print(f"{kw}: {count}")

    # 保存到 CSV
    save_results_to_csv(results, total_counter, OUTPUT_CSV)
    print(f"\nResults saved to {OUTPUT_CSV}")
    print(f"Total elapsed time: {elapsed:.2f} seconds")
    print(f"Total files processed: {len(results)}")
    print(f"Total size: {total_bytes_all / (1024*1024):.2f} MB")
    print(f"Total lines: {total_lines_all}")